import React, { useContext, useState } from 'react';
import { SignupContext } from '../home/SignupContext';
import { Link } from "react-router-dom";
import './Nav.css';
import { FaListUl } from "react-icons/fa";

const Nav = () => {
    const [showDrop,setShowDrop]= useState(false);
    const {showSignup, setShowSignup} = useContext(SignupContext);
    const [showLogin, setShowLogin] = useState(false);

    const loginOpen = ()=>{
     
        return(
            <div className='loginOpen'  >
            <h1>Sign In</h1>
            <label>Username</label>
            <input className='loginInput'/>
            <label >Password</label>
            <input className='loginInput'/>
        </div>
        );
     
    }
    const loginHide = ()=>{
        return(
            <div className='loginHide'  >
            <h1>Sign In</h1>
            <label>Username</label>
            <input className='loginInput'/>
            <label >Password</label>
            <input className='loginInput'/>
        </div>
        )
    }
    return (
        <div >
            
            <div className='nav'>
                <div className='Container'>
                <div className='navItemCont'>
                    <FaListUl className='listIcon'
                    onClick={()=>{
                        if(showDrop===true){
                            setShowDrop(false);
                        }
                        else
                          setShowDrop(true);
                        
                    }}
                    />
                   
                    <Link className='navItem' id='home'>Home</Link>
                    <Link className='navItem'>About</Link>
                    <Link className='navItem' id="cont">Contact</Link>
                </div>
                <div className='SignInReg'>
                    <div className='signIn'
                    onClick={()=>{
                        
                        if(showLogin===true){
                            setShowLogin(false);
                        }
                        else {
                            setShowLogin(true);
                            
                        }
                    }}
                    >Sign In</div>
                    <div className='reg'
                    
                    onClick={()=>{setShowSignup(true)}}
                    >Register</div>
                </div>
                </div>
               
            </div>
            {
                showDrop? (
<div className='navDropDown'>
                    <Link className='navItemD'>Home</Link>
                    <Link className='navItemD'>About</Link>
                    <Link className='navItemD' id="cont">Contact</Link>
                    </div>
                ) :""
            }
        
                       {/* {
                showLogin? (
<div className='login'  >
                    <h1>Sign In</h1>
                    <label>Username</label>
                    <input className='loginInput'/>
                    <label >Password</label>
                    <input className='loginInput'/>
                </div>
            
                ) : ""
               }   
                 */
                 loginOpen()}
        </div>
    );
}

export default Nav;
